$_['doc_pagespeed_image_lazy_load'] = "<b>Enable Lazy load images</b> - this function is responsible for setting the loading=\"lazy\" or bus-loading=\"lazy\" attribute to &lt;img tags, &lt;iframe, &lt;frame. In some templates, it is necessary to replace data-src with src (native browser version) or data-busloadinglazy-src (universal module version) via modifiers or replacement functionality in the html code.<br /><textarea class=\"form-control\">// api bus_loading_lazy.js
/*
busLoadingLazy.browser['name'] - get browser name
busLoadingLazy.browser['version'] - get browser version
busLoadingLazy.setting['browser']['name'] - run only for the browser name (after executing the code, you need to remove the restriction)
busLoadingLazy.setting['browser']['version'] - run only for browser version and newer (restriction must be removed after code execution)
busLoadingLazy.setting['elements'] - handle page elements, default '[loading=\"lazy\"][data-busloadinglazy-src], [loading=\"lazy\"][data-busloadinglazy-id]'
busLoadingLazy.setting['element'] - handle page element, default is ''
busLoadingLazy.setting['fps'] - frequency of actions of any events after which your code will fire in the busLoadingLazy event, by default 10
busLoadingLazy.setting['offsetTop'] - distance from the browser window to the element that starts loading, default 50
busLoadingLazy.setting['width'] - minimum width to launch a page element, default 0
busLoadingLazy.setting['lazy'] - forced element loading, false by default
busLoadingLazy.setting['start'] - start loading element from those found from number, default 0
busLoadingLazy.setting['quantity'] - process the number of elements from all found, default 10
busLoadingLazy.setting['exception'] - array containing data-busloadinglazy-src and data-busloadinglazy-id values to be excluded from processing, default {}

// Example:
window.addEventListener('busLoadingLazyBefore', function() {
	busLoadingLazy.setting['status'] = false; // don't load the module
	busLoadingLazy.setting['start'] = 0; // start download from
	busLoadingLazy.setting['quantity'] = 20; // load so much
	busLoadingLazy.setting['exception'] = {'https://mysite.by/image.png':true}; // don't load this image on page load
	busLoadingLazy.start(); // load more images and iframes when loading the page, starting from 50 in the amount of 5 pieces
	busLoadingLazy.setting['exception'] = {}; // remove exceptions
});
*/

// script bus_loading_lazy.js is launched when the page is fully loaded by the load event. at that point it can get a valid height, and so on.
// execute the code on scroll, resize according to the launch frequency settings busLoadingLazy.setting['fps']
window.addEventListener('busLoadingLazy', function() {
	/* your code */
});

// execute the code before running the busLoadingLazy script
window.addEventListener('busLoadingLazyBefore', function() {
	/* your code */
});

// execute code after running busLoadingLazy script
window.addEventListener('busLoadingLazyAfter', function() {
	/* your code */
});

// execute the code when the delayed block appears
window.addEventListener('busLoadingLazyId-93fd76d319a4d202930d925a170c45ff', function() {
	/* your code */
})

// process the html element now (if you want to load on click)
busLoadingLazy.start({element:'[data-busloadinglazy-id=\'93fd76d319a4d202930d925a170c45ff\']', lazy:true});

// more correct launch on click
function myclick() {
	if ('busLoadingLazy' in window) {
		busLoadingLazy.start({element:'[data-busloadinglazy-id=\'93fd76d319a4d202930d925a170c45ff\']', lazy:true});
	} else {
		window.addEventListener('busLoadingLazyBefore', function() {
			busLoadingLazy.start({element:'[data-busloadinglazy-id=\'93fd76d319a4d202930d925a170c45ff\']', lazy:true});
		});
	}
}</textarea>";
